import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

function ConfirmationPage() {
    const { state } = useLocation();
    const { bookingId } = state || {}; // Assuming you pass bookingId as state
    const [bookingDetails, setBookingDetails] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch booking details when component is mounted
        const fetchBookingDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/get-booking/${bookingId}`);
                setBookingDetails(response.data);
                setLoading(false);
            } catch (err) {
                setError('Error: Could not fetch booking details');
                setLoading(false);
            }
        };

        if (bookingId) {
            fetchBookingDetails();
        }
    }, [bookingId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    if (!bookingDetails) {
        return <div>Error: No booking details found!</div>;
    }

    return (
        <div className="flex justify-center items-center min-h-screen bg-slate-500">
            <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-2xl">
                <h2 className="text-2xl font-bold text-gray-800 mb-6">Booking Confirmation</h2>
                <p className="text-lg text-green-600 mb-6 font-semibold">Your table has been successfully confirmed!</p>
                <div className="mb-4">
                    <p><strong>Guest Name:</strong> {bookingDetails.firstName} {bookingDetails.lastName}</p>
                    <p><strong>Email:</strong> {bookingDetails.email}</p>
                    <p><strong>Phone:</strong> {bookingDetails.phone}</p>
                    <p><strong>Date:</strong> {bookingDetails.date}</p>
                    <p><strong>Time:</strong> {bookingDetails.time}</p>
                    <p><strong>Guests:</strong> {bookingDetails.guests}</p>
                    <p><strong>Seating Preference:</strong> {bookingDetails.seatingPreference}</p>
                    <p><strong>Special Requests:</strong> {bookingDetails.specialRequests}</p>
                </div>
                <div className="flex justify-center">
                    <button onClick={() => navigate('/')} className="bg-blue-600 text-white px-6 py-2 rounded-md">
                        Back to Home
                    </button>
                </div>
            </div>
        </div>
    );
}

export default ConfirmationPage;
