import React, { useState } from 'react';

const BookingForm = () => {
  const [formData, setFormData] = useState({
    date: '',
    time: '',
    guests: '',
    name: '',
    contact: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Basic client-side validation
    if (!formData.date || !formData.time || !formData.guests || !formData.name || !formData.contact) {
      alert('All fields are required.');
      return;
    }

    try {
      const response = await fetch('/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert('Reservation successful!');
        setFormData({ date: '', time: '', guests: '', name: '', contact: '' });
      } else {
        alert('Failed to make a reservation.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="date" name="date" value={formData.date} onChange={handleChange} required />
      <input type="time" name="time" value={formData.time} onChange={handleChange} required />
      <input type="number" name="guests" value={formData.guests} onChange={handleChange} min="1" required />
      <input type="text" name="name" value={formData.name} onChange={handleChange} required placeholder="Your Name" />
      <input type="text" name="contact" value={formData.contact} onChange={handleChange} required placeholder="Your Contact Info" />
      <button type="submit">Book Now</button>
    </form>
  );
};

export default BookingForm;
