import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookingList = () => {
    const [bookings, setBookings] = useState([]);
    const [error, setError] = useState(null); // For error handling
    const [loading, setLoading] = useState(true); // For loading state

    // Fetch bookings on component mount
    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const response = await axios.get('http://localhost:5000/get-booking/');
                setBookings(response.data);
            } catch (err) {
                setError('Error fetching bookings.');
                console.error('Error fetching bookings:', err);
            } finally {
                setLoading(false); // Set loading to false after data is fetched
            }
        };
        fetchBookings();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/delete-booking/${id}`);
            setBookings((prevBookings) => prevBookings.filter((booking) => booking.id !== id));
        } catch (err) {
            setError('Error deleting booking.');
            console.error('Error deleting booking:', err);
        }
    };

    if (loading) {
        return (
            <div className="flex items-center justify-center h-screen">
                <div className="text-center">
                    <p className="text-lg font-semibold text-gray-700">Loading bookings...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-3xl">
                <h2 className="text-2xl font-bold text-center mb-6">Booking List</h2>
                {error && <p className="text-red-500 text-center mb-4">{error}</p>}
                {bookings.length > 0 ? (
                    <ul className="space-y-4">
                        {bookings.map((booking) => (
                            <li
                                key={booking.id}
                                className="flex justify-between items-center p-4 border border-gray-300 rounded-lg"
                            >
                                <div>
                                    <p className="font-semibold text-lg">
                                        {booking.firstName} {booking.lastName}
                                    </p>
                                    <p className="text-gray-500">
                                        {booking.date} at {booking.time}
                                    </p>
                                </div>
                                <button
                                    onClick={() => handleDelete(booking.id)}
                                    className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
                                >
                                    Delete
                                </button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p className="text-center text-gray-500">No bookings found.</p>
                )}
            </div>
        </div>
    );
};

export default BookingList;
