import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookingForm = () => {
    const [formData, setFormData] = useState({
        date: '',
        time: '',
        guests: '',
        name: '',
        contact: ''
    });

    const [message, setMessage] = useState('');
    const [availableSlots, setAvailableSlots] = useState([]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const fetchAvailableSlots = async () => {
        try {
            const response = await axios.get('/api/bookings');
            const existingBookings = response.data;
            // Logic to calculate available slots can be implemented here
            setAvailableSlots(existingBookings);
        } catch (error) {
            console.error('Error fetching available slots:', error);
        }
    };

    useEffect(() => {
        fetchAvailableSlots();
    }, [formData.date]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('/api/bookings', formData);
            setMessage(response.data.message);
            fetchAvailableSlots();
        } catch (error) {
            setMessage(error.response?.data?.message || 'An error occurred.');
        }
    };

    return (
        <div className="max-w-md mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Restaurant Table Booking</h1>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block mb-1">Date</label>
                    <input type="date" name="date" value={formData.date} onChange={handleChange} required className="w-full border p-2" />
                </div>
                <div>
                    <label className="block mb-1">Time</label>
                    <input type="time" name="time" value={formData.time} onChange={handleChange} required className="w-full border p-2" />
                </div>
                <div>
                    <label className="block mb-1">Number of Guests</label>
                    <input type="number" name="guests" value={formData.guests} onChange={handleChange} required className="w-full border p-2" />
                </div>
                <div>
                    <label className="block mb-1">Name</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required className="w-full border p-2" />
                </div>
                <div>
                    <label className="block mb-1">Contact</label>
                    <input type="text" name="contact" value={formData.contact} onChange={handleChange} required className="w-full border p-2" />
                </div>
                <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Book Now</button>
            </form>
            {message && <p className="mt-4 text-center text-green-600">{message}</p>}
        </div>
    );
};

export default BookingForm;
