import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookingList = () => {
    const [bookings, setBookings] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    // Fetch bookings on component mount
    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const response = await axios.get('http://localhost:5000/get-booking/');
                setBookings(response.data);
            } catch (err) {
                setError('Error fetching bookings.');
                console.error('Error fetching bookings:', err);
            } finally {
                setLoading(false);
            }
        };
        fetchBookings();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/delete-booking/${id}`);
            setBookings(prevBookings => prevBookings.filter(booking => booking.id !== id));
        } catch (err) {
            setError('Error deleting booking.');
            console.error('Error deleting booking:', err);
        }
    };

    if (loading) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <p className="text-lg font-medium text-gray-600">Loading bookings...</p>
            </div>
        );
    }

    return (
        <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
            <h2 className="text-2xl font-bold mb-6 text-gray-800">Booking List</h2>
            {error && <p className="text-red-500 mb-4">{error}</p>}
            <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-2xl">
                {bookings.length > 0 ? (
                    <ul className="space-y-4">
                        {bookings.map((booking) => (
                            <li key={booking.id} className="flex justify-between items-center border-b pb-2">
                                <div>
                                    <p className="text-lg font-medium text-gray-700">{booking.firstName} {booking.lastName}</p>
                                    <p className="text-sm text-gray-500">{booking.date} at {booking.time}</p>
                                </div>
                                <button
                                    className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                                    onClick={() => handleDelete(booking.id)}
                                >
                                    Delete
                                </button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p className="text-center text-gray-600">No bookings found.</p>
                )}
            </div>
        </div>
    );
};

export default BookingList;
